const JSONAPIFactory = require('@cardstack/test-support/jsonapi-factory');
module.exports = function() {
  // Article Card Schema
  let factory = new JSONAPIFactory();

  return factory.getDocumentFor(factory.addResource('card-definitions')
    .withRelated('model', factory.addResource('content-types')
      .withAttributes({ name: 'articles' })
      .withRelated('fields', [
        { type: 'fields', id: 'local-hub::person-card::name' },
        factory.addResource('fields').withAttributes({
          name: 'title',
          'is-metadata': true,
          'needed-when-embedded': true,
          'field-type': '@cardstack/core-types::string'
        }),
        factory.addResource('fields').withAttributes({
          name: 'body',
          'is-metadata': true,
          'field-type': '@cardstack/core-types::string'
        }),
        factory.addResource('fields').withAttributes({
          name: 'categories',
          'is-metadata': true,
          'needed-when-embedded': true,
          'field-type': '@cardstack/core-types::has-many'
        }).withRelated('related-types', [
          // this is modeling an enumeration
          factory.addResource('content-types').withAttributes({ name: 'categories' })
        ]),
        factory.addResource('fields').withAttributes({
          name: 'author',
          'is-metadata': true,
          'needed-when-embedded': true,
          'field-type': '@cardstack/core-types::belongs-to'
        }).withRelated('related-types', [{ type: 'content-types', id: 'cards' }])
      ])
    ));
};