const JSONAPIFactory = require('@cardstack/test-support/jsonapi-factory');
module.exports = function() {
  let factory = new JSONAPIFactory();
  // Person Card Schema
  factory.addResource('card-definitions', 'local-hub::person-card')
    .withRelated('model', factory.addResource('content-types', 'people')
      .withRelated('fields', [
        factory.addResource('fields').withAttributes({
          name: 'name',
          'is-metadata': true,
          'needed-when-embedded': true,
          'field-type': '@cardstack/core-types::string'
        }),
        factory.addResource('fields').withAttributes({
          name: 'password',
          'field-type': '@cardstack/core-types::string'
        }),
      ])
    );
  return factory.getModels(); // intentionally getting all models in the factory
};