const JSONAPIFactory = require('@cardstack/test-support/jsonapi-factory');
module.exports = function({sourceId, packageName}) {
  let factory = new JSONAPIFactory(sourceId, packageName);
  // Person Card Schema
  factory.addResource('content-types', 'people')
    .withRelated('fields', [
      factory.addResource('fields').withAttributes({
        name: 'name',
        'is-metadata': true,
        'needed-when-embedded': true,
        'field-type': '@cardstack/core-types::string'
      }),
      factory.addResource('fields').withAttributes({
        name: 'password',
        'field-type': '@cardstack/core-types::string'
      }),
    ]);
  return factory.getModels(); // Intentionally not getting the card-definitions models
};